"use client";

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import { FaUser, FaCreditCard, FaSignOutAlt, FaEdit, FaCheck, FaTimes } from 'react-icons/fa';
import { supabase } from '@/lib/supabaseClient';

interface UserProfile {
  id: string;
  user_id: string;
  display_name: string;
  avatar_url: string;
  created_at: string;
  updated_at: string;
}

interface Subscription {
  id: string;
  user_id: string;
  stripe_customer_id: string;
  stripe_subscription_id: string;
  status: string;
  current_period_end: string;
  created_at: string;
  updated_at: string;
}

export default function AccountPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [userEmail, setUserEmail] = useState<string>('');
  const [userId, setUserId] = useState<string>('');
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [subscription, setSubscription] = useState<Subscription | null>(null);
  const [isEditingName, setIsEditingName] = useState(false);
  const [displayName, setDisplayName] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const router = useRouter();

  useEffect(() => {
    checkAuthAndLoadData();
  }, []);

  const checkAuthAndLoadData = async () => {
    try {
      const { data: { session } } = await supabase.auth.getSession();

      if (!session) {
        router.push('/login');
        return;
      }

      setUserEmail(session.user.email || '');
      setUserId(session.user.id);

      await Promise.all([
        loadProfile(session.user.id),
        loadSubscription(session.user.id)
      ]);

      setIsLoading(false);
    } catch (error) {
      console.error('Error loading account data:', error);
      setIsLoading(false);
    }
  };

  const loadProfile = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('user_profiles')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error && error.code !== 'PGRST116') {
        console.error('Error loading profile:', error);
        return;
      }

      if (data) {
        setProfile(data);
        setDisplayName(data.display_name || '');
      } else {
        const newProfile = {
          user_id: userId,
          display_name: userEmail.split('@')[0],
          avatar_url: `https://ui-avatars.com/api/?name=${encodeURIComponent(userEmail)}&background=e50914&color=fff&size=200`,
        };

        const { data: createdProfile, error: createError } = await supabase
          .from('user_profiles')
          .insert([newProfile])
          .select()
          .single();

        if (createError) {
          console.error('Error creating profile:', createError);
        } else {
          setProfile(createdProfile);
          setDisplayName(createdProfile.display_name || '');
        }
      }
    } catch (error) {
      console.error('Error in loadProfile:', error);
    }
  };

  const loadSubscription = async (userId: string) => {
    try {
      const { data, error } = await supabase
        .from('subscriptions')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error && error.code !== 'PGRST116') {
        console.error('Error loading subscription:', error);
        return;
      }

      if (data) {
        setSubscription(data);
      }
    } catch (error) {
      console.error('Error in loadSubscription:', error);
    }
  };

  const handleSaveDisplayName = async () => {
    if (!profile || !displayName.trim()) return;

    setIsSaving(true);
    try {
      const { error } = await supabase
        .from('user_profiles')
        .update({ display_name: displayName.trim(), updated_at: new Date().toISOString() })
        .eq('id', profile.id);

      if (error) {
        console.error('Error updating display name:', error);
        alert('Failed to update display name');
      } else {
        setProfile({ ...profile, display_name: displayName.trim() });
        setIsEditingName(false);
      }
    } catch (error) {
      console.error('Error saving display name:', error);
      alert('Failed to update display name');
    } finally {
      setIsSaving(false);
    }
  };

  const handleCancelEdit = () => {
    setDisplayName(profile?.display_name || '');
    setIsEditingName(false);
  };

  const handleSignOut = async () => {
    await supabase.auth.signOut();
    router.push('/');
  };

  const handleCancelSubscription = async () => {
    if (!subscription) return;

    const confirmed = confirm('Are you sure you want to cancel your subscription? You will lose access to all content.');
    if (!confirmed) return;

    try {
      const { error } = await supabase
        .from('subscriptions')
        .update({ status: 'canceled', updated_at: new Date().toISOString() })
        .eq('id', subscription.id);

      if (error) {
        console.error('Error canceling subscription:', error);
        alert('Failed to cancel subscription');
      } else {
        alert('Subscription canceled successfully');
        await loadSubscription(userId);
      }
    } catch (error) {
      console.error('Error canceling subscription:', error);
      alert('Failed to cancel subscription');
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
        return 'text-green-400';
      case 'canceled':
        return 'text-red-400';
      case 'past_due':
        return 'text-yellow-400';
      default:
        return 'text-gray-400';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[var(--background)] flex items-center justify-center pt-20">
        <div className="w-16 h-16 border-4 border-[var(--primary)] border-t-transparent rounded-full animate-spin"></div>
      </div>
